mapper.select:
SELECT A.*, M.nickname AS writer, IFNULL(M.nickname, "탈퇴회원") AS extra__writer FROM article AS A LEFT JOIN `member` AS M ON A.memberId = M.id WHERE 1
AND A.boardId = #{boardId}
AND title LIKE CONCAT('%', #{searchKeyword}, '%') AND `body` LIKE CONCAT('%', #{searchKeyword}, '%') AND (title LIKE CONCAT('%', #{searchKeyword}, '%') OR `body` LIKE CONCAT('%', #{searchKeyword}, '%')) ORDER BY A.id DESC
LIMIT #{limitStart}, #{limitTake}

mapper.update:
UPDATE article


		updateDate = NOW(),


		title = #{title},


		`body` = #{body}


WHERE id = #{id}
